package skill1;
import java.util.*;
public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a;
		a = sc.nextInt();
		long b = a;
		float c = b;
		System.out.println("int: "+a+" long: "+b+" float: "+c);
		

	}

}
